import React from 'react';

function Sidebar({ sections, onAdd, onSelect, isAdmin }) {
  return (
    <div style={{ width: '200px', borderRight: '1px solid gray', padding: '10px' }}>
      {isAdmin && <button onClick={onAdd}>➕ Nueva Sección</button>}
      <ul>
        {sections.map((s) => (
          <li key={s.id} onClick={() => onSelect(s.id)} style={{ cursor: 'pointer', marginTop: '10px' }}>
            {s.title}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Sidebar;
