import React, { useState, useEffect } from 'react';
import Sidebar from '../components/Sidebar';
import Editor from '../components/Editor';

function DocsPage() {
  // Leer al iniciar desde localStorage
  const [sections, setSections] = useState(() => {
    const saved = localStorage.getItem('docs_sections');
    return saved ? JSON.parse(saved) : [];
  });

  const [selectedId, setSelectedId] = useState(() => {
    const saved = localStorage.getItem('docs_sections');
    const parsed = saved ? JSON.parse(saved) : [];
    return parsed.length > 0 ? parsed[0].id : null;
  });

  // Guardar cada vez que se actualicen las secciones
  useEffect(() => {
    localStorage.setItem('docs_sections', JSON.stringify(sections));
  }, [sections]);

  // Agregar nueva sección
  const handleAddSection = () => {
    const title = prompt("Título de la nueva sección:");
    if (!title) return;
    const newSection = {
      id: Date.now(),
      title,
      content: ''
    };
    setSections(prev => [...prev, newSection]);
    setSelectedId(newSection.id);
  };

  // Seleccionar sección desde el menú
  const handleSelect = (id) => setSelectedId(id);

  // Actualizar contenido
  const handleUpdateContent = (newContent) => {
    setSections(prev =>
      prev.map(sec =>
        sec.id === selectedId ? { ...sec, content: newContent } : sec
      )
    );
  };

  const selectedSection = sections.find(sec => sec.id === selectedId);

  return (
    <div style={{ display: 'flex' }}>
      <Sidebar sections={sections} onAdd={handleAddSection} onSelect={handleSelect} />
      <Editor section={selectedSection} onUpdateContent={handleUpdateContent} />
    </div>
  );
}

export default DocsPage;
