import React, { useState, useEffect } from 'react';

function DocsPage() {
  const [text, setText] = useState('');

  // Cargar al iniciar
  useEffect(() => {
    const saved = localStorage.getItem('my_text');
    if (saved) {
      setText(saved);
    }
  }, []);

  // Guardar cada vez que cambie
  useEffect(() => {
    localStorage.setItem('my_text', text);
  }, [text]);

  return (
    <div style={{ padding: '20px' }}>
      <h2>Editor Persistente</h2>
      <textarea
        style={{ width: '100%', height: '300px' }}
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <p>Este contenido se guarda autom√°ticamente en localStorage.</p>
    </div>
  );
}

export default DocsPage;
