import React from 'react';

function Editor({ section, onUpdateContent }) {
  if (!section) return <div style={{ margin: '10px' }}>Selecciona una sección</div>;

  const handleContentChange = (e) => {
    const newContent = e.target.value;
    onUpdateContent({ ...section, content: newContent });
  };

  const handleTitleChange = (e) => {
    const newTitle = e.target.value;
    onUpdateContent({ ...section, title: newTitle });
  };

  return (
    <div style={{ flex: 1, padding: '10px' }}>
      <input
        style={{ fontSize: '20px', width: '100%', marginBottom: '10px' }}
        value={section.title}
        onChange={handleTitleChange}
        placeholder="Título de la sección"
      />
      <textarea
        style={{ width: '100%', height: '400px' }}
        value={section.content}
        onChange={handleContentChange}
        placeholder="Escribe tu documentación aquí..."
      />
    </div>
  );
}

export default Editor;
