import { useEffect, useState } from 'react';
import MarkdownViewer from './components/MarkdownViewer';
import docsList from './docsList';
import './styles.css';

function App() {
  const [selectedDoc, setSelectedDoc] = useState('index.md');
  const [theme, setTheme] = useState(() => localStorage.getItem('theme') || 'light');

  const currentIndex = docsList.indexOf(selectedDoc);

  const goBack = () => {
    if (currentIndex > 0) setSelectedDoc(docsList[currentIndex - 1]);
  };

  const goNext = () => {
    if (currentIndex < docsList.length - 1) setSelectedDoc(docsList[currentIndex + 1]);
  };

  useEffect(() => {
    document.body.className = theme;
    localStorage.setItem('theme', theme);
  }, [theme]);

  return (
    <div>
      <header>
        üìò Documentaci√≥n SO
        <button className="theme-toggle" onClick={() => setTheme(t => (t === 'light' ? 'dark' : 'light'))}>
          {theme === 'light' ? 'üåô Oscuro' : '‚òÄÔ∏è Claro'}
        </button>
      </header>

      <main>
        <div style={{ display: 'flex', alignItems: 'flex-start' }}>
          <div style={{ marginRight: '1rem', display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
            <button onClick={goBack} disabled={currentIndex === 0}>‚¨ÖÔ∏è Anterior</button>
            <button onClick={goNext} disabled={currentIndex === docsList.length - 1}>‚û°Ô∏è Siguiente</button>
          </div>
          <MarkdownViewer file={selectedDoc} onNavigate={setSelectedDoc} />
        </div>
      </main>
    </div>
  );
}

export default App;
