import React, { useState, useEffect } from 'react';
import Sidebar from '../components/Sidebar';
import Editor from '../components/Editor';

function DocsPage() {
  const [sections, setSections] = useState([]);
  const [selectedId, setSelectedId] = useState(null);

  // Cargar datos desde localStorage al iniciar
  useEffect(() => {
    const saved = localStorage.getItem('docs_sections');
    if (saved) {
      const parsed = JSON.parse(saved);
      setSections(parsed);
      if (parsed.length > 0) setSelectedId(parsed[0].id); // seleccionar primero
    }
  }, []);

  // Guardar automáticamente cuando cambie `sections`
  useEffect(() => {
    localStorage.setItem('docs_sections', JSON.stringify(sections));
  }, [sections]);

  const handleAddSection = () => {
    const title = prompt("Título de la nueva sección:");
    if (!title) return;
    const newSection = {
      id: Date.now(),
      title,
      content: ''
    };
    const updated = [...sections, newSection];
    setSections(updated);
    setSelectedId(newSection.id);
  };

  const handleSelect = (id) => {
    setSelectedId(id); // cambios ya están guardados automáticamente
  };

  const handleUpdateContent = (newContent) => {
    setSections(sections.map(sec =>
      sec.id === selectedId ? { ...sec, content: newContent } : sec
    ));
  };

  const selectedSection = sections.find(sec => sec.id === selectedId);

  return (
    <div style={{ display: 'flex' }}>
      <Sidebar sections={sections} onAdd={handleAddSection} onSelect={handleSelect} />
      <Editor section={selectedSection} onUpdateContent={handleUpdateContent} />
    </div>
  );
}

export default DocsPage;
